mapper.update:
UPDATE ARTICLE
   SET VIEW_CNT = VIEW_CNT +1
 WHERE ARTICLE_ID = #{_parameter}

mapper.insert:
INSERT INTO ARTICLE
	 ( ARTICLE_ID
 	 , UPPER_ARTICLE_ID
	 , ARTICLE_TITLE
	 , ARTICLE_CONTENT
	 , ARTICLE_PASSWORD
	 , ARTICLE_SALT )
VALUES
	 ( #{articleId}
	 , #{upperArticleId}
	 , #{articleTitle}
	 , #{articleContent}
	 , #{articlePassword}
	 , #{articleSalt} )

mapper.update:
UPDATE ARTICLE_MASTER
   SET MEMBER_NAME = #{memberName}
	 , ARTICLE_MODIFY_DATE = SYSDATE
 WHERE ARTICLE_ID = #{articleId}

UPDATE ARTICLE
   SET ARTICLE_TITLE = #{articleTitle}
	 , ARTICLE_CONTENT = #{articleContent}
 WHERE ARTICLE_ID = #{articleId}

UPDATE ARTICLE_MASTER
   SET ARTICLE_DELETE_DATE = SYSDATE
 WHERE ARTICLE_ID = #{_parameter}
